mapper.select:
select 

from kl_location
where id = #{id,jdbcType=INTEGER}
  and device_id = #{deviceId,jdbcType=VARCHAR}
  and vendor = #{vendor,jdbcType=VARCHAR}
  and product_id = #{productId,jdbcType=VARCHAR}

mapper.delete:
delete from kl_location
where id = #{id,jdbcType=INTEGER}
  and device_id = #{deviceId,jdbcType=VARCHAR}
  and vendor = #{vendor,jdbcType=VARCHAR}
  and product_id = #{productId,jdbcType=VARCHAR}
